SELECT a.column_id,a.column_name,a.data_type,a.density,a.nullable,a.num_nulls,a.num_distinct,a.num_buckets,a.low_value,a.high_value,a.sample_size,a.last_analyzed,a.data_default,b.comments FROM all_tab_columns a, all_col_comments b WHERE 1=1 AND a.owner = b.owner AND a.table_name = b.table_name AND a.column_name = b.column_name AND a.owner = 'SCOTT' AND a.table_name= 'EMP' ORDER BY column_id